import { Announcement } from '@material-ui/icons';
import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import styled from 'styled-components'
import { Footer } from '../components/Footer';
import Navbar from '../components/Navbar';
import { Newsletter } from '../components/Newsletter';

const Container = styled.div``;
const Product = styled.div`
    display:flex;
    justify-content:space-between;
    margin-bottom:10px;
`;

const ProductDetail = styled.div`
    flex:2;
    display:flex;
`;

const Image = styled.img`
    width:200px;
`;

const Details = styled.div`
    padding:20px;
    display:flex;
    flex-direction: column;
    justify-content: space-around;
`;

const ProductName = styled.span``;

const ProductId = styled.span``;

const PriceDetail = styled.div`
    flex:1;
    display:flex;
    flex-direction:column;
    align-items:center;
    justify-content: center;
`;
const ProductPrice = styled.div`
    font-size: 30px;
    font-weight: 200;
`;

const Hr = styled.hr`
    margin: 5px 5px;
`;



export const Myorders = () => {
    const user = useSelector((state)=>state.user.currentUser);
    /* const orderid = async () => {
        function status() {
          const url = 'http://localhost:5000/api/orders/find/'+user._id;
          return axios.get(url).then((resp) => {
            return resp.data;
          });
        }
        const data = await status();
        var dt = data.products;
        return dt
      }; */
      /* const getOrderById = (id)=> {
        return axios.get('http://localhost:5000/api/orders/find/' + id)
            .then(response => {
              this.response = response.data
              return this.response
            })
        }*/
       /*  orderid()
            .then(data => {
                console.log(data);
  }); */
  const [posts, setPosts] = useState([]);

 /*  useEffect(() => {
    axios.get('http://localhost:5000/api/orders/find/'+user._id)
    .then(res => setPosts(res.data))
    .catch(err => console.log(err));
  }, [])
  console.log(posts.products) */

  const expensesListResp = async () => {
    await axios.get('http://localhost:5000/api/orders/find/'+user._id).then(res => setPosts(res.data[0].products))
  }
  expensesListResp();
  

  return (
    <Container>
        <Navbar/>
        <Announcement/>
        console.log(posts);
        {posts.map(product => (
                    <Product>
                    <ProductDetail>
                        <Image src={product.img}/>
                        <Details>
                            <ProductName><b>Product:</b>{product.title}</ProductName>
                            <ProductId><b>ID:</b> {product.productId}</ProductId>
                        </Details>
                    </ProductDetail>
                    <PriceDetail>
                        <ProductPrice>{product.price}</ProductPrice>
                    </PriceDetail>
                    <Hr/>                    
                    </Product>
                    ))}
        <Newsletter/>
        <Footer/>
    </Container>
  )
}
