import { Badge } from '@material-ui/core';
import { AccountBoxOutlined, AccountCircleOutlined, AccountCircleRounded, Search, ShoppingCartOutlined } from '@material-ui/icons'
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { logout } from '../redux/apiCalls';
import {mobile} from "../responsive";

const Container = styled.div`
    height: 60px;
    ${mobile({height : "50px"})};
`;
const Wrapper = styled.div`
    padding: 10px 20px;
    display: flex ;
    align-items: center;
    justify-content: space-between ;
    ${mobile({padding : "10px 0px"})};
`;
const Language = styled.span`
    fonst-size: 14px;
    cursor: pointer;
    ${mobile({display : "none"})};
`;
const SearchContainer = styled.div`
    border: 0.5px solid lightgray;
    display: flex;
    align-items: center;
    margin-left: 25px;
    padding: 5px;
`;
const Input = styled.input`
    border: none;
    ${mobile({width : "50px"})};
`;

const Left = styled.div`
    flex: 1;
    display: flex;
    align-items: center;
`;
const Right = styled.div`
    flex: 1;
    display: flex;
    align-items: center;
    justify-content: flex-end;
    ${mobile({flex:2,justifyContent : "center"})};
`;

const MenuItem = styled.div`
    font-size: 14px;
    cursor: pointer;
    margin-left: 25px;
    ${mobile({fontSize : "12px",marginLeft:"10px"})};
    &:disabled{
    color: green;
    cursor: not-allowed;
  }
`;

const Centre = styled.div`
    flex: 1;
    text-align: center;
`;

const Logo = styled.h1`
    font-weight: bold;
    ${mobile({fontSize : "24px"})};
`;

const Button = styled.button`

`;

const Navbar = () => {
    const quantity = useSelector(state=>state.cart.quantity);
    const user = useSelector((state)=>state.user.currentUser);
    const dispatch = useDispatch();

    const handleClick = (e)=>{
        logout(dispatch);
      }

  return (
    <Container>
    <Wrapper>
        <Left>
            <Language>EN</Language>
            <SearchContainer>
                <Input placeholder="search"/>
                <Search style={{color:"gray",fontSize:16}}/>
            </SearchContainer>
        </Left>
        <Centre><Link to="/"><Logo>Fund!T</Logo></Link></Centre>
        <Right>
        <Link to="/register">
        <MenuItem disabled={user}>Register</MenuItem>
        </Link>
        <MenuItem><Link to="/login">Sign in</Link></MenuItem>
        <Button onClick={handleClick} disabled={user}>Logout</Button>
        <MenuItem>GetFunding</MenuItem>
        <MenuItem>
        <Link to="/cart">
        <Badge badgeContent={quantity} color="secondary">
            <ShoppingCartOutlined/>
        </Badge>
        </Link>
        </MenuItem>
        <MenuItem>
            <AccountCircleOutlined/>
        </MenuItem>
        </Right>
    </Wrapper>
    </Container>
  )
}

export default Navbar
