import styled from "styled-components"
import Announcement from "../components/Announcement";
import { Footer } from "../components/Footer";
import Navbar from "../components/Navbar";
import { Newsletter } from "../components/Newsletter";

const Container = styled.div``;
const Wrapper = styled.div`
    padding:50px;
    display:flex;
`;

const ImgContainer = styled.div`
    flex:1;
`;

const Image = styled.img`
    width:100%;
    height:90vh;
    object-fit:cover;
`;

const InfoContainer = styled.div`
    flex:1;
    padding: 0px 50px;
`;

const Title = styled.h1`
    font-weight:200;
`;

const Desc = styled.p`
    margin:20px 0px;
`;

const RewardContainer = styled.div``;

const Reward = styled.div``;

const RewardTitle = styled.div``;

const Price = styled.span`
    font-weight:100;
    font-size:40px;
`;

export const Product = () => {
  return (
    <Container>
        <Navbar/>
        <Announcement/>
        <Wrapper>
            <ImgContainer>
                <Image src="https://i.ibb.co/nB42ZF6/eric-nopanen-8e0-EHPUx3-Mo-unsplash.jpg"/>
            </ImgContainer>
            <Desc>this is a product with amazing sound</Desc>
            <InfoContainer>
                <Title>antique speaker</Title>
                <RewardContainer>
                    <Reward>
                        <RewardTitle>Pledge 100rs or more</RewardTitle>
                        <Desc>You will get 100 reward points but not any goodies</Desc>
                    </Reward>
                    <Reward>
                        <RewardTitle>Pledge 1000rs or more</RewardTitle>
                        <Desc>You will get 100 reward points in addition to that you will get special disks to run on speaker</Desc>
                    </Reward>
                    <Reward>
                        <RewardTitle>Pledge 10000rs or more</RewardTitle>
                        <Desc>You will get speaker and all additional goodies</Desc>
                    </Reward>
                </RewardContainer>
                <Price>20$</Price>
            </InfoContainer>
        </Wrapper>
        <Newsletter/>
        <Footer/>
    </Container>
  )
}
